@media screen {

    html,body,main {
        height: 100%;
        width: 100%;
        background-color: rgb(204, 163, 156);
        display: flex;
        align-items: center;
        justify-content: space-around;
        flex-direction: column;
        user-select: none;
    }

    body{
        font-size: 100%;
        font-family: Arial, Helvetica, sans-serif;
    }


#theHeader{
    background: linear-gradient(rgb(151,157, 166), rgb(112, 94, 120), rgb(129, 47, 51));
    padding: 0 50px 0 50px;
    width: auto;
    height: auto;
    border: 5px solid rgb(109, 41, 44);
    border-radius: 20px;
    display: flex;
    flex-direction: row;
}

.title {
    transition: transform 300ms;
    text-shadow: -2px -2px 0 rgba(26, 26, 26, 0.5);
    font: normal normal 100px cursive;
    margin: 0;
}
.titleNoSound{
    transition: transform 300ms;
    text-shadow: -2px -2px 0 rgba(26, 26, 26, 0.5);
    font: normal normal 70px cursive;
    display: flex;
    flex-direction: column;
}

h2 {
    transition: transform 300ms;
    text-shadow: -2px -2px 0 rgba(26, 26, 26, 0.5);
    font: normal normal 40px cursive;
    margin: 30% 5% 0 5%;   
    display:block;
}

.theContent {
    width: 80%;
}

.form {
    border: 0.2em solid rgb(38, 52, 101);
    background: linear-gradient(rgb(151,157, 166), rgb(104, 120, 140), rgb(58, 73, 128));
    width: 70%;
    margin: auto;
    padding: 3em 1em 3em 1em;
    border-radius: 0.4em; /* arrotondamento angoli */
    text-align: center;
    display: flex;
    justify-content: space-around;
    flex-direction: column;
    align-content: center;
}

.selectionContainer {
    display: flex;
    justify-content: space-between;
}

.form input[type=submit], input[type=button] {
    cursor: pointer;
    width:60%;
    height:70px;
    border: none;
    margin: 30px;
    position: relative;
    text-align:center;
    color: #F2F2F2;
    outline: none;
    border-radius: 6px;
    font: normal normal 2em/1 Verdana;
    text-decoration: none;
    background: linear-gradient(rgb(38, 52, 101), #3a4980);
    text-shadow: -2px -2px 0 rgba(26, 26, 26, 0.5);
    box-shadow: inset 0 -1px 1px #3d4f8f, 0 8px 0 #202c58, 0 10px 15px rgba(0, 0, 0, 0.35);
    transition: all 100ms ease-in-out;
}

.form input[type=submit]:hover, input[type=button]:hover {
    background: linear-gradient(#334175, #455281);
    color: #a3ffeb;
}

.form label {
    color: rgb(38, 52, 101);
    text-shadow: -1px -1px 0 rgba(26, 26, 26, 0.5);
    display: inline-block;
    width: 25%;
    font: normal normal 1.5em Verdana;
}

.custom-select {
    margin-right: 80px;
    width: 200px;
    position: relative;
    font: normal normal 1em Verdana;
    display: inline-block;
    width:40%;
    margin: 0 5% 5% 1%;
}

.custom-select select {
    display: none; /*hide original SELECT element: */
}

.select-selected {
    background-color: rgb(38, 52, 101);
    border-radius: 3px;
}


/* Style the arrow inside the select element: */
.select-selected:after {
    position: absolute;
    content: "";
    top: 17px;
    right: 12px;
    width: 0;
    height: 0;
    border: 6px solid transparent;
    border-color: #F2F2F2 transparent transparent transparent;
}

/* Point the arrow upwards when the select box is open (active): */
.select-selected.select-arrow-active:after {
    border-color: transparent transparent #F2F2F2 transparent;
    top: 7px;
}

/* style the items (options), including the selected item: */
.select-items div,.select-selected {
    color: #F2F2F2;
    padding: 8px 16px;
    border: 1px solid transparent;
    border-color: transparent transparent rgba(0, 0, 0, 0.1) transparent;
    cursor: pointer;
}

/* Style items (options): */
.select-items {
    position: absolute;
    background-color: rgb(53, 73, 145);
    top: 100%;
    left: 0;
    right: 0;
    z-index: 99;
}

/* Hide the items when the select box is closed: */
.select-hide {
    display: none;
}

.select-items div:hover, .same-as-selected {
    background-color: rgba(255, 255, 255, 0.1);
}

}

/* --------------------------------------------- play button PlayGame ---------------------------------------- */
.buttonPlay:hover{
    background: linear-gradient(#334175, #455281);
    color: #a3ffeb;
}
/* Basic red round button properties */
.buttonPlay {
  place-items: center;
  float: left;
  display:block;
  position: relative;
  width: 60px;
  height: 60px;
  border-radius: 35px;
  background-color: rgb(129, 47, 51);
  color: white;
  cursor: pointer;
  /* Hide the text 'play', which is present in the HTML document for accessibility */
  font-size: 0;
  margin: 1% 1% 1% 1%;
}

/* Properties for the pseudo-element that almost every button will need.
   You can just merge it into the style below if you are only going to have
   the play button. */
.buttonPlay::before {
  content: "";
  display: block;
  position: absolute;
}

/* Play button properties using CSS shape */
.buttonPlay::before {
  width: 0;
  height: 0;
  border-bottom: 20px solid transparent;
  border-top: 20px solid transparent;
  border-left: 28px solid white;
  top: 10px;
  left: 20px;
}

/* --------------------------------------------- END play button PlayGame ------------------------------------- */

