@media screen {

    html,body,main {
        height: 100%;
        width: 100%;
        margin: 0;
        background-color: #000000;
        display: flex;
        align-items: center;
        justify-content: center;
        user-select: none;
    }
    
#content {
    height: 130px;
    width: 130px;
    margin: 0;
    background-image: radial-gradient(ellipse, rgb(204, 163, 156), black 73%);
    display: flex;
    align-items: center;
    justify-content: center;
    user-select: none;   
}

.backgroundAnimation {
    animation: backgroundAnimation 2s cubic-bezier(0.69, 0.07, 0.18, 0.99) forwards;
}

@keyframes backgroundAnimation {
    0% {width: 130px; height: 130px}
    100% {width: 100vw; height: 100vh}
}

#theHeader{
    background: linear-gradient(rgb(151,157, 166), rgb(112, 94, 120), rgb(129, 47, 51));
    width: 0;
    height: 0;
    border-radius: 20px;
    border: 0px solid rgb(109, 41, 44);
    display: flex;
    align-items: center;
    justify-content: center;
    transition: width 2s cubic-bezier(0.69, 0.07, 0.18, 0.99), height 2s cubic-bezier(0.69, 0.07, 0.18, 0.99), border 2s cubic-bezier(0.69, 0.07, 0.18, 0.99);
    box-sizing: border-box;
}

#startAudio {
    position: absolute;
    height: 100px;
    width: 100px;
    border-color: rgb(129, 47, 51);
    border-radius: 50%;
    background-color: rgb(242, 242, 242);
    color: rgb(242, 242, 242);
    transition: opacity 1.2s cubic-bezier(0.69, 0.07, 0.18, 0.99), background-color 1s;
    z-index: +1;
    cursor: pointer;
}
#startAudio:hover {
    background-color: rgb(74, 108, 247);
}

.title {
    display: inline-block;
    opacity: 0.0;
    transition: opacity 800ms, transform 300ms;
    text-shadow: -2px -2px 0 rgba(26, 26, 26, 0.5);
    font: normal normal 170px cursive;
    margin: 0;
}

}

