/* --------------------------------------------- inizio @media screen ----------------------------------------- */
@media screen {

    html,body,main {
        height: 100%;
        width: 100%;
        background-color: #d8821131;
        display: flex;
        align-items: center;
        justify-content: space-around;
        flex-direction: column;
        user-select: none;
    }
    body{
        font-size: 100%;
        font-family: Arial, Helvetica, sans-serif;
    }



/* --------------------------------------------- CARTOON game ------------------------------------------- */
.cartoon {
    position: fixed;
    padding: 30px 20px 30px 20px;
    width:auto;
    margin:0 18.5em 2em;
    color:#000;
    background:#EF6559;
    border: 2px solid black;
    border-radius:10px;
    font: normal normal 1em Verdana;
    visibility: hidden;
}

.cartoon:after {
    content:"";
    position:absolute;
    bottom:-15px;
    border-width:15px 15px 0;
    border-style:solid;
    border-color:#EF6559 transparent;
    display:block;
    width:0;
}
/* --------------------------------------------- ENDcartoon PlayGame ---------------------------------------- */

/* ------------------------------------------------ timer PlayGame ------------------------------------------ */
#timer {
    font-family: sans-serif;
    color: black;
    display: grid;
    place-items: center;
    float: right;
    display:block;
    margin: 0 0 1% 0;
}

.base-timer {
    position: relative;
    width: 100px;
    height: 100px;
}

.base-timer__svg {
    transform: scaleX(-1);
}

.base-timer__circle {
    fill: none;
    stroke: none;
}

.base-timer__path-elapsed {
    stroke-width: 10px;
    stroke: rgba(128, 128, 128, 0);
}

.base-timer__path-remaining {
    stroke-width: 10px;
    stroke-linecap: round;
    transform: rotate(90deg);
    transform-origin: center;
    transition: 1s linear all;
    fill-rule: nonzero;
    stroke: currentColor;
}

.base-timer__path-remaining.green {
    color: rgba(28, 143, 62, 0.863);
}

.base-timer__path-remaining.orange {
    color: rgb(255, 166, 0);
}

.base-timer__path-remaining.red {
    color: red;
}

.base-timer__label {
    position: absolute;
    width: 100px;
    height: 100px;
    top: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 30px;
}
/* ------------------------------------------------ END timer PlayGame ---------------------------------------- */

/* ----------------------------------------------- bar content PlayGame --------------------------------------- */
#fPlayGame {
    height: 100%;
    width: 100%;
    background-color: #d8821131;
    display: flex;
    align-items: center;
    justify-content: space-around;
    flex-direction: column;
    padding: 30px 0 0 0;
    user-select: none;
}

#buttonsContainer {
    width: 1300px;
    height: auto;
}

.button {
    color: #DADAF1;
    background: linear-gradient(#263465, #3a4980);
    text-shadow: -2px -2px 0 rgba(26, 26, 26, 0.5);
    box-shadow: inset 0 -1px 1px #3d4f8f, 0 8px 0 #202c58, 0 10px 15px rgba(0, 0, 0, 0.35);
    text-align: center;
    width: 17%;
    margin:0 1% 2% 0%;
    border-radius: 5px;
    padding: 1%;
    cursor: pointer;
    float: left;
    display:block;
    font: normal normal 1.5em Verdana;
}

.button:hover{
    background: linear-gradient(#354272, #4d5881);
    color: #ADC5EF;
}

#finishGame{
    float:right;
    font-size: 100%;
    width: 20%;
    border: none;
    position: relative;
    text-align:center;
    display: inline-block;
    outline: none;
    border-radius: 6px;
    text-decoration: none;
    font: normal normal 1.5em Verdana;
    margin:0 1% 2% 0%;
}

#bar {
    background-color: #DADAF1;
    padding:10px;
    border-radius: 8px;
    height: 130px;
    min-height: 130px;
    width: 1300px;
    display: flex;
    flex-flow: row wrap;
    justify-content: space-around;
    border: 3px solid black;
}

.tile_v {
    width: 60px;
    height: 120px;
    border-radius: 5px;
    background-color: black;
    cursor: pointer;
    display: flex;
    flex-flow: column wrap;
    justify-content: space-between;
    align-self: center;
    box-sizing: content-box;
}

.tile_h {
    width: 120px;
    height: 60px;
    border-radius: 5px;
    background-color: black;
    cursor: pointer;
    display: flex;
    flex-flow: row wrap;
    justify-content: space-between;
    align-self: center;
}

.tileUpper {
    width: 60px;
    height: 60px;
    border-radius: 3px;
    background-color: black;
    cursor: pointer;
    border: 2px solid black;
    box-sizing: border-box;
}

.tileLower {
    width: 60px;
    height: 60px;
    border-radius: 3px;
    background-color: black;
    cursor: pointer;
    box-sizing: border-box;
    border: 2px solid black;
}

/* ------------------------------------------ END bar content PlayGame ------------------------------------------ */

/* ---------------------------------------------------- TABLE --------------------------------------------------- */
#table {
    display: flex;
    background-color: #DADAF1;
    border: 3px solid black;
    border-radius: 10px;
    flex-direction: column;
    margin: 1% 0 0 0;
}

.row {
    background-color: #DADAF1;
    display: flex;
    border-radius: 10px;
    width: auto;
    align-items: flex-start;
    justify-content: space-around;
    padding-top: 5px;
}

.box {
    background-color: #e4e4f0;
    color: black;
    border-radius: 3px;
    border: 1px dotted black;
    box-sizing: border-box;
}

/* --------------------------------------------------  END TABLE ------------------------------------------------------ */

}
  /* ----------------------------------------------- END @media screen -------------------------------------------------- */

